import React, { Component } from 'react';
import { Card, CardImg, CardText, CardBody, CardTitle, Breadcrumb, BreadcrumbItem, Button } from 'reactstrap';
import { Link } from 'react-router-dom';


function RenderDish({ dish }) {
    if (dish != null) {
        return (
            <div className="col-12 col-md-5 m-1">
                <Card>
                    <CardImg width="100%" src={dish.image} alt={dish.name} />
                    <CardBody>
                        <CardTitle>{dish.name}</CardTitle>
                        <CardText>
                            {dish.description}
                        </CardText>
                    </CardBody>
                </Card>
            </div>


        );
    }
    else {
        return (
            <div></div>
        );
    }
}

function RenderComments(props) {
    const dish = props.comments;
    if (dish != null) {
        const cmts = dish.map(comment => {
            return (
                <li key={comment.id}>
                    <p>{comment.comment}</p>
                    <p>-- {comment.author},
                        &nbsp;
                        {new Intl.DateTimeFormat('en-US', {
                        year: 'numeric',
                        month: 'long',
                        day: '2-digit'
                    }).format(new Date(comment.date))}
                    </p>
                </li>
            )
        });
        return (
            <div className='col-12 col-md-5 m-1'>
                <h4> Comments </h4>
                <ul className='list-unstyled'>
                    {cmts}
                  
                </ul>
                <CommentForm/>
            </div>
        );
    }
    else {
        return (
            <div></div>
        );
    }

}




class CommentForm extends Component {
    constructor(props) {
        super(props);
    }
    render() {
        return (
            <div>
                <Button outline onClick={this.toggleModal} color="secondary"><span className="fa fa-pencil fa-lg"></span> Submit Comment</Button>{' '}
            </div>
        );
    }

}



export default function Dishdetail(props) {
    const dishItem = <RenderDish dish={props.dish} />
    const commentItem = <RenderComments comments={props.comments} />
    return (
        <div className="container">
            <div className="row">
                <Breadcrumb>
                    <BreadcrumbItem><Link to="/menu">Menu</Link></BreadcrumbItem>
                    <BreadcrumbItem active>{props.dish.name}</BreadcrumbItem>
                </Breadcrumb>
                <div className="col-12">
                    <h3>{props.dish.name}</h3>
                    <hr />
                </div>
            </div>
            <div className="row">
                {dishItem}
                {commentItem}
            </div>

        </div>

    )
}
