import React, { Component } from 'react';
import Menu from './MenuComponent';
import Header from './HeaderComponent';
import Dishdetail from './DishdetailComponent';
import Footer from './FooterComponent';
import Home from './HomeComponent';
import Contact from './ContactComponent';
import About from './AboutComponent';
import { Switch, Route, Redirect, withRouter } from 'react-router-dom';
import { connect } from 'react-redux';


const mapStateToProps = state => {
    return {
      dishes: state.dishes,
      comments: state.comments,
      promotions: state.promotions,
      leaders: state.leaders
    }
  }
  
class Main extends Component {
    constructor(props) {
        super(props);
    }
    render() {
        const HomePage = () => {
            const dish = this.props.dishes.filter((dish) => dish.featured)[0];
            const promotion = this.props.promotions.filter((promo) => promo.featured)[0];
            const leader = this.props.leaders.filter((leader) => leader.featured)[0];
            return (
                <Home dish={dish} promotion={promotion} leader={leader} />
            );
        };

        const DishWithId = ({ match }) => {
            return (
                <Dishdetail dish={this.props.dishes.filter((dish) => dish.id === parseInt(match.params.dishId, 10))[0]}
                    comments={this.props.comments.filter((comment) => comment.dishId === parseInt(match.params.dishId, 10))} />
            );
        };

        return (
            <div>
                <Header />
                <div className="container">
                    <Switch>
                        <Route path='/home' component={HomePage} />
                        <Route exact path='/menu' component={() => <Menu dishes={this.props.dishes} />} />
                        <Route path='/menu/:dishId' component={DishWithId}></Route>
                        <Route exact path='/contactus' component={Contact} />
                        <Route exact path='/aboutus' component={() => <About leaders={this.props.leaders}/>} />
                        <Redirect to="/home" />
                    </Switch>
                </div >
                <Footer />
            </div >
        );
    }
}


export default withRouter(connect(mapStateToProps)(Main));
